import React, { Component } from "react";
import {
  View,
  Text,
  ImageBackground,
  Dimensions,
  Image,
  TextInput,
  KeyboardAvoidingView,
  TouchableOpacity,
  ScrollView,
  StatusBar,
  StyleSheet
} from "react-native";
import container from "../../Styles/Container/style";
let width = Dimensions.get("window").width;
let height = Dimensions.get("window").height;
import Color from "../../Config/Color";
import Icon from "react-native-vector-icons/Ionicons";
import style from "./style";
import TextIcon from "../../Component/TextIcon";
import Button from "../../Component/Button/index";
import RadioForm, { RadioButton, RadioButtonInput, RadioButtonLabel } from 'react-native-simple-radio-button';
import RNPickerSelect from 'react-native-picker-select';
import AsyncStorage from '@react-native-community/async-storage';
import { Picker } from '@react-native-picker/picker';




export default class index extends Component {

  constructor(props) {
    super(props)


    var plats = ""
    var plongs = ""
    var dlats = ""
    var dlongs = ""
    var daddresss = ""
    var pstreets = ""

    this.state = {
      itemName: "",
      description: "",
      quantity: "",
      weight: "",
      items: "Choose Item",




    }

  }



  processItems = async () => {

    if (this.state.itemName === "") {
      alert("Item Name cannot be empty")
    }
    else if (this.state.description === "") {
      alert("Description cannot be empty")
    }
    else if (this.state.quantity === "") {
      alert("Quantity Cannot be empty")
    }


    else if (this.state.items === "") {
      alert("Item Type cannot be empty")
    }
    else {

      await AsyncStorage.setItem('@storage_itemWeight', this.state.weight)

      await AsyncStorage.setItem('@storage_itemName', this.state.itemName)
      await AsyncStorage.setItem('@storage_Description', this.state.description)
      await AsyncStorage.setItem('@storage_Quantity', this.state.quantity)
      await AsyncStorage.setItem('@storage_Items', this.state.items)

      this.props.navigation.navigate("OrderSummary", { platitude: plats, plongitude: plongs, dlatitude: dlats, dlongitude: dlongs, deaddress: daddresss, pestreet: pstreets, Ride: ride })
    }
  }









  render() {

    plats = this.props.navigation.state.params.platitude
    plongs = this.props.navigation.state.params.plongitude
    dlats = this.props.navigation.state.params.dlatitude
    dlongs = this.props.navigation.state.params.dlongitude
    daddresss = this.props.navigation.state.params.deaddress
    pstreets = this.props.navigation.state.params.pestreet
    ride = this.props.navigation.state.params.Ride



    return (
      <View style={[container.container]}>
        <StatusBar
          barStyle="light-content"
        />
        <View style={{ width: width, backgroundColor: '#000' }}>
          <View
            style={{
              marginHorizontal: 15,
              marginTop: 40,
            }}
          >
            <TouchableOpacity
              onPress={() => this.props.navigation.goBack()}
            >
              <Icon
                name="ios-arrow-back"
                size={25}
                style={[
                  {
                    alignSelf: "flex-start",
                    color: "#fff"
                    //marginTop:40
                  },
                  this.props.iconStyle
                ]}
              />
            </TouchableOpacity>
            <View style={{ flexDirection: "row", }}>
              <View style={{ flex: 0.75, justifyContent: "center" }}>
                <Text
                  style={{
                    fontSize: 26,
                    fontFamily: "uber-b",
                    color: Color.white,
                    marginTop: 10,
                    marginBottom: 25
                  }}
                >
                  Create Shipment (3/3)
                  </Text>
              </View>
              {/* <View style={[style.imageProfileView, { flex: 0.25 }]}>
                  <Image
                    source={require("../../Image/Pic.png")}
                    style={style.imageProfile}
                  />
                </View> */}
            </View>
          </View>
        </View>
        <View />


        <ScrollView>
          <View style={styles.centralize}>
            <Text style={styles.centralize}>Select Items to be Delivered</Text></View>
          <View style={styles.regForm}>





            <View style={{ borderWidth: 1, borderColor: 'black', borderRadius: 4, marginBottom: 30 }}>
              <Picker selectedValue={this.state.selectedValue} onValueChange={(value) => this.setState({ selectedValue: value, items: value })}>
                <Picker.Item label="Choose Category" value="" />
                <Picker.Item label="Food" value="Food" />
                <Picker.Item label="Clothings" value="Clothings" />
                <Picker.Item label="Documents" value="Documents" />
                <Picker.Item label="Health Products" value="healthproducts" />
                <Picker.Item label="Jewelries/Accessories" value="Accessories" />
                <Picker.Item label="Computer Accessories" value="computeraccessories" />
                <Picker.Item label="Others" value="Others" />
              </Picker>
            </View>


            <TextInput style={styles.textinput} placeholder="Item Name" underlineColorAndroid={'transparent'} onChangeText={(val) => this.setState({ itemName: val })} />

            <TextInput style={styles.textinput} placeholder="Description" underlineColorAndroid={'transparent'} onChangeText={(val) => this.setState({ description: val })} />

            <TextInput style={styles.textinput} placeholder="Quantity" underlineColorAndroid={'transparent'} onChangeText={(val) => this.setState({ quantity: val })} />
            <TextInput style={styles.textinput} placeholder="Weight (kg)" underlineColorAndroid={'transparent'} onChangeText={(val) => this.setState({ weight: val })} />




          </View>
        </ScrollView>
        <Button
          Text="Order Summary"
          viewStyle={{
            backgroundColor: Color.black,
            color: '#ffffff',
            borderRadius: 0,
            borderColor: Color.white,
            marginTop: 10,
            marginBottom: 0,
            marginHorizontal: 0
          }}
          textStyle={{ color: Color.steel }}
          onPress={() => this.processItems()}
        />

      </View>
    );
  }
}

const styles = StyleSheet.create({
  regForm: {
    padding: 20
  },
  textinput: {


    marginBottom: 30,
    color: '#000000',
    borderColor: '#000000',
    borderWidth: 1,
    padding: 10,
    fontSize: 12
  },
  centralize: {
    alignItems: 'center',
    fontWeight: 'bold',
    fontSize: 15,
    marginVertical: 10

  }
});
