import React, { Component } from "react";
import {
  View,
  Text,
  ImageBackground,
  Dimensions,
  Image,
  KeyboardAvoidingView,
  TouchableOpacity,
  StatusBar,
  Alert
} from "react-native";
import container from "../../Styles/Container/style";
let width = Dimensions.get("window").width;
let height = Dimensions.get("window").height;
import { Tab, Tabs } from "native-base";
import Tab1 from "./signUp";
import Connection from "../Connection";
import Tab2 from "./signIn";
import Color from "../../Config/Color";
import style from './style';
import Spinner from 'react-native-loading-spinner-overlay';
import AsyncStorage from '@react-native-community/async-storage';
import * as Location from 'expo-location';
import * as Permissions from 'expo-permissions';

export default class componentName extends Component {
  constructor(props) {
    super(props);
    this.state = {

      tmobile: "",
      spinner: false,
      isConnected: true,
      tpassword: "",
      datasource: "",
      firstname: "",
      lastname: "",
      signupmobile: "",
      signuppassword: "",
      signupemail: "",
      activation: "",
      vemail: ""
    };


  }

  async componentDidMount() {
    this.getLocationAsync();
    this.clearAsyncStorage();
    //this.getCurrentLocation();

  }

  async getLocationAsync() {
    // permissions returns only for location permissions on iOS and under certain conditions, see Permissions.LOCATION
    const { status, permissions } = await Permissions.askAsync(Permissions.LOCATION);
    if (status === 'granted') {
      return Location.getCurrentPositionAsync({ enableHighAccuracy: true });
    } else {
      throw new Error('Location permission not granted');
    }
  }



  clearAsyncStorage = async () => {
    const asyncStorageKeys = await AsyncStorage.getAllKeys();
    if (asyncStorageKeys.length > 0) {
      if (Platform.OS === 'android') {
        await AsyncStorage.clear();
      }
      if (Platform.OS === 'ios') {
        await AsyncStorage.multiRemove(asyncStorageKeys);
      }
    }
  }


  signupHandler = async (semail, smobile, spassword) => {

    this.setState({
      signupemail: semail,
      signupmobile: smobile,
      signuppassword: spassword

    }, () => {

      if (this.state.signupemail === "") {
        alert("Please enter an email address")
      }
      else if (this.state.signupmobile === "") {
        alert("Please enter mobile phone")
      }

      else if (this.state.isConnected === false) {
        alert("You are not connected to the internet")
      }

      else if (this.state.signuppassword === "") {
        alert("Please enter password")
      }
      else {

        let reg = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
        if (reg.test(this.state.signupemail) === false) {
          console.log("Email is Not Correct");
          alert("Please enter a valid email")
        }
        else {

          this.setState({
            spinner: true
          }, () => {



            fetch('https://expressalllogistics.com/logistics/androidappsignup.php', {
              method: 'POST',
              headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
              },
              body: JSON.stringify({
                eemail: this.state.signupemail,
                emobile: this.state.signupmobile,
                epassword: this.state.signuppassword

              })

            }).then((response) => response.json())
              .then((responseJson) => {

                this.setState({
                  spinner: false


                }, () => {

                  var d = responseJson.Tripcodes;

                  var r = responseJson.activation;

                  if (d === 4) {
                    alert("Mobile Already Exists, Kindly Sign in to Continue")
                  }
                  else if (d === 2) {
                    this.setState({
                      activation: r


                    }, () => {


                      this._activation()
                      this._activationmobile()

                      // alert("Account Successfully created")
                      this.props.navigation.navigate("OTP")
                    });


                  }
                  else {
                    alert("A problem occured, Please try again....")
                  }


                });

              })

          })

        }


      }
    })
  }



  submitHandler = async (mobile, password) => {



    this.setState({
      tmobile: mobile,
      tpassword: password,

    }, () => {

      if (this.state.tmobile === "") {
        alert("Please enter mobile phone")
      }
      else if (this.state.isConnected === false) {
        alert("You are not connected to the internet")
      }

      else if (this.state.tpassword === "") {
        alert("Please enter password")
      }
      else {

        this.setState({
          spinner: true
        });

        try {



          fetch('https://expressalllogistics.com/logistics/hybridlogin.php', {
            method: 'POST',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({

              mobile: this.state.tmobile,
              password: this.state.tpassword

            })

          }).then((response) => response.json())
            .then((responseJson) => {

              this.setState({
                spinner: false

              })



              var user_id = responseJson.Tripcode;

              //var success = responseJson.Success;

              var cfirstname = responseJson.Firstname;

              var clastname = responseJson.Lastname;

              var cemail = responseJson.Email;

              this.setState({
                firstname: cfirstname,
                lastname: clastname,
                vemail: cemail
              });



              console.log(user_id)
              console.log(this.state.firstname)

              // If server response message same as Data Matched
              if (user_id === 1) {



                //Then open Profile activity and send user email to profile activity.
                //this.props.navigation.navigate('Second', { Email: UserEmail });



                this._realData()

                this.props.navigation.navigate("Home")

              }
              else if (user_id === 10) {
                alert("Account not yet Activated")
              }
              else {

                //Then open Profile activity and send user email to profile activity.
                //this.props.navigation.navigate('Second', { Email: UserEmail });
                /*
                                  Alert.alert(
                                    "Ooops",
                                    "Invalid Username or Password",
                                    [
                                      {
                                        text: "Cancel",
                                        onPress: () => console.log("Cancel Pressed"),
                                        style: "cancel"
