import React, { Component } from "react";
import {
  Text,
  View,
  TextInput,
  KeyboardAvoidingView,
  TouchableOpacity
} from "react-native";
import container from "../../Styles/Container/style";
import Color from "../../Config/Color";
import Button from "../../Component/Button/index";
export default class signUP extends Component {

  constructor(props) {
    super(props);

    this.state = {
      semail: "",
      spassword: "",
      smobile: ""
    }
  }

  render() {
    return (
      <KeyboardAvoidingView style={container.container} behavior='padding' enabled keyboardVerticalOffset={30}>
        <View style={{ marginVertical: 20, marginHorizontal: 15 }}>
          <TextInput
            style={{
              borderColor: Color.steel,
              borderWidth: 0.5,
              borderRadius: 5,
              fontSize: 18,
              marginVertical: 10,
              paddingHorizontal: 15,
              paddingVertical: 7,
              fontFamily: "uber-r"
            }}
            autoCapitalize='none'
            autoFocus={true}
            placeholderTextColor={Color.steel}
            placeholder="Email address"
            underlineColorAndroid={"transparent"}
            keyboardType="email-address"
            onChangeText={(val) => this.setState({ semail: val })}
          />
          <TextInput
            style={{
              borderColor: Color.steel,
              borderWidth: 0.5,
              borderRadius: 5,
              fontSize: 18,
              marginVertical: 10,
              paddingHorizontal: 15,
              paddingVertical: 7,
              fontFamily: "uber-r"
            }}
            keyboardType="phone-pad"
            placeholderTextColor={Color.steel}
            placeholder="Mobile number"
            underlineColorAndroid={"transparent"}
            onChangeText={(val) => this.setState({ smobile: val })}
          />

          <TextInput
            style={{
              borderColor: Color.steel,
              borderWidth: 0.5,
              borderRadius: 5,
              fontSize: 18,
              marginVertical: 10,
              paddingHorizontal: 15,
              paddingVertical: 7,
              fontFamily: "uber-r"
            }}
            autoCapitalize='none'
            placeholderTextColor={Color.steel}
            placeholder="Password"
            secureTextEntry
            underlineColorAndroid={"transparent"}
            onChangeText={(val) => this.setState({ spassword: val })}
          />
        </View>
        <Button
          Text="Sign Up"
          textStyle={{ fontSize: 15, fontWeight: "100" }}
          onPress={() => this.props.signupHandler(this.state.semail, this.state.smobile, this.state.spassword)}
        />



      </KeyboardAvoidingView>
    );
  }
}
